import React, {Component} from 'react';

class Resumen extends Component {
    render(){
        const {marca, year, plan} = this.props.datos;
        return(
            <div className="resumen">
            <h2>Resumen de cotización</h2>
            <ul>
                <li>Marca: {marca}</li>
                <li>Plan: {plan}</li>
                <li>Año del Auto: {year}</li>
            </ul>

            </div>
        )

    }
}

export default Resumen;
